import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import TextField from '../InputComponents/TextField';
import { updateKeySkills, addArrayElement, removeArrayElement } from '../../ReduxManager/dataStoreSlice';
import BottomNavigation from './BottomNavigation';
import Sidebar from './Sidebar'; // Import Sidebar

function KeySkills(props) {
  const skillHeads = useSelector(state => state.dataStore.skills);
  const dispatch = useDispatch();

  function AddSkill() {
    dispatch(addArrayElement({
      key: 'skills',
      element: { skillName: "" }
    }));
  }

  function RemoveSkill() {
    dispatch(removeArrayElement({ key: "skills" }));
  }

  return (
    <div className="d-flex" style={{ minHeight: '100vh' }}>
      <Sidebar />

      <div className="p-5" style={{ marginLeft: '270px', width: '100%' }}>
        <h1 className="mb-3">Key Skills</h1>
        <hr />

        {skillHeads.map((item, index) => (
          <div key={index} className='col-lg-5 col-md-6 col-12 mt-4'>
            <TextField
              type="text"
              value={item.skillName}
              onChange={(value) => {
                dispatch(updateKeySkills({
                  key: 'skillName',
                  value: value,
                  index: index,
                }));
              }}
            />
          </div>
        ))}

        <div className='row mt-4'>
          <div className='col-sm-2 col-12 mt-3'>
            <button className='btn btn-primary w-100' onClick={AddSkill}>Add Skill</button>
          </div>
          <div className='col-sm-2 col-12 mt-3'>
            <button className='btn btn-danger w-100' onClick={RemoveSkill}>Remove</button>
          </div>
        </div>

        <div className="mt-4">
          <BottomNavigation
            prevPagePath='/detailsfillingpage/education'
            nextPagePath='/myresume'
            isFormValid={props.isFormValid}
          />
        </div>
      </div>
    </div>
  );
}

export default KeySkills;
