import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import { updateState } from '../../ReduxManager/dataStoreSlice';
const shortid = require('shortid');

// Example data with public demo images â€” replace with your own paths!
const templateImagesPaths = [
  {
    name: "Modern Blue",
    imageSource: "https://via.placeholder.com/300x400/007bff/ffffff?text=Modern+Blue",
    category: "Modern",
  },
  {
    name: "Classic Black",
    imageSource: "https://via.placeholder.com/300x400/000000/ffffff?text=Classic+Black",
    category: "Classic",
  },
  {
    name: "Creative Purple",
    imageSource: "https://via.placeholder.com/300x400/6f42c1/ffffff?text=Creative+Purple",
    category: "Creative",
  },
];

function Home() {
  const [isMouseOver, setIsMouseOver] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('All');
  const dispatch = useDispatch();

  const categories = ['All', ...new Set(templateImagesPaths.map((t) => t.category))];

  // Filter templates by search term and category
  const filteredTemplates = templateImagesPaths.filter(template => {
    const matchName = template.name.toLowerCase().includes(searchTerm.toLowerCase());
    const matchCategory = selectedCategory === 'All' || template.category === selectedCategory;
    return matchName && matchCategory;
  });

  // Fallback image if actual image fails to load
  const handleImageError = (e) => {
    e.target.onerror = null; // Prevent infinite loop
    e.target.src = "https://via.placeholder.com/300x400?text=No+Image";
  };

  return (
    <>
      <style>{`
        .template-card {
          border: 1px solid #d1e3ff;
          border-radius: 10px;
          overflow: hidden;
          transition: all 0.3s ease-in-out;
          cursor: pointer;
          background: white;
          height: 470px;
          display: flex;
          flex-direction: column;
          justify-content: space-between;
        }
        .template-card:hover {
          box-shadow: 0px 4px 20px rgba(0, 123, 255, 0.3);
          transform: translateY(-5px);
        }
        .template-image-container {
          position: relative;
          flex-grow: 1;
          padding: 10px;
          display: flex;
          justify-content: center;
          align-items: center;
        }
        .template-image-container img {
          max-height: 380px;
          max-width: 100%;
          object-fit: contain;
          border-radius: 8px;
          transition: transform 0.3s ease-in-out;
          display: block;
        }
        .btn-container {
          padding: 10px;
          text-align: center;
        }
      `}</style>

      <div className="pb-5" style={{ minWidth: '300px', background: '#f5f8ff' }}>
        <div className="d-flex justify-content-center mt-5">
          <h3
            className="p-3 rounded shadow text-center text-primary fw-bold"
            style={{
              backgroundColor: '#e9f5ff',
              border: '2px solid #007bff',
              fontFamily: 'Segoe UI, sans-serif',
              fontSize: '1.8rem',
              letterSpacing: '1px',
              width: 'fit-content',
            }}
          >
            Select a Resume Template to Get Started!
          </h3>
        </div>

        <div className="container mt-4">
          <div className="row mb-4 align-items-center">
            <div className="col-md-6 mb-2">
              <input
                type="text"
                className="form-control"
                placeholder="Search template by name..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                autoComplete="off"
              />
            </div>
            <div className="col-md-6 mb-2">
              <select
                className="form-select"
                value={selectedCategory}
                onChange={(e) => setSelectedCategory(e.target.value)}
              >
                {categories.map((cat) => (
                  <option key={cat} value={cat}>
                    {cat}
                  </option>
                ))}
              </select>
            </div>
          </div>

          <div className="row">
            {filteredTemplates.length === 0 && (
              <div className="text-center mt-4 text-muted">
                <p>No templates found.</p>
              </div>
            )}
            {filteredTemplates.map((template) => (
              <div
                className="col-lg-3 col-md-6 col-12 mt-4 d-flex align-items-stretch"
                key={shortid.generate()}
                onMouseOver={() => setIsMouseOver(template.name)}
                onMouseOut={() => setIsMouseOver('')}
              >
                <div className="template-card shadow-sm">
                  <div className="w-100 text-center mt-2">
                    <h5 className="fw-bold text-dark">{template.name}</h5>
                  </div>

                  <div className="template-image-container">
                    <img
                      src={template.imageSource}
                      alt="Resume Template"
                      onError={handleImageError}
                      style={{
                        borderRadius: '5px',
                        transform: isMouseOver === template.name ? 'scale(1.03)' : 'scale(1)',
                      }}
                    />
                    {isMouseOver === template.name && (
                      <div
                        className="position-absolute top-50 start-50 translate-middle d-flex flex-column align-items-center"
                        style={{ zIndex: 10 }}
                      >
                        <Link to="/detailsfillingpage/personalinfo">
                          <button
                            className="btn btn-primary btn-sm mb-2"
                            title="Start with this template"
                            onClick={() =>
                              dispatch(
                                updateState({
                                  key: 'selectedTemplate',
                                  value: template.name,
                                })
                              )
                            }
                          >
                            Use Template
                          </button>
                        </Link>

                        <button
                          className="btn btn-outline-secondary btn-sm"
                          title="Preview this template"
                          onClick={() => window.open(template.imageSource, '_blank')}
                        >
                          Preview
                        </button>
                      </div>
                    )}
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </>
  );
}

export default Home;
