import React, { useState } from 'react';
import { useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import jsPDF from 'jspdf';
import html2canvas from 'html2canvas';
import Template1 from '../TemplatesComponents/Template1';
import Template2 from '../TemplatesComponents/Template2';
import Template3 from '../TemplatesComponents/Template3';
import Template4 from '../TemplatesComponents/Template4';
import SuccessMessage from './Modal';

function MyResume() {
  const selectedTemplate = useSelector((state) => state.dataStore.selectedTemplate);
  const [showModal, setShowModal] = useState(false);
  const [isLoading, setIsLoading] = useState(false);

  const downloadComponentPDF = () => {
    const input = document.getElementById('divToPrint');
    setIsLoading(true);
    html2canvas(input, { scrollY: -window.scrollY }).then((canvas) => {
      const imgData = canvas.toDataURL('image/png');
      const pdf = new jsPDF('p', 'px', 'a4');
      const ratio = canvas.width / canvas.height;
      const width = pdf.internal.pageSize.getWidth();
      const height = width / ratio;
      pdf.addImage(imgData, 'JPEG', 0, 0, width, height);
      pdf.save('resume.pdf');
    }).then(() => {
      setIsLoading(false);
      setShowModal(true);
      setTimeout(() => {
        setShowModal(false);
      }, 6000);
    });
  };

  const renderTemplate = () => {
    switch (selectedTemplate) {
      case 'Template 1':
        return <Template1 />;
      case 'Template 2':
        return <Template2 />;
      case 'Template 3':
        return <Template3 />;
      case 'Template 4':
        return <Template4 />;
      default:
        return (
          <div style={{ textAlign: 'center', color: 'red', fontWeight: 'bold' }}>
            <h3>Please select a template!</h3>
          </div>
        );
    }
  };

  return (
    <div style={{ display: 'flex', minHeight: '100vh', backgroundColor: '#f5f5f5' }}>
      {/* Sidebar */}
      <div style={{
        width: '200px',
        backgroundColor: '#ffffff',
        padding: '20px',
        borderRight: '1px solid #ccc',
        display: 'flex',
        flexDirection: 'column',
        gap: '20px'
      }}>
        <h3 style={{ color: '#07588a' }}>Menu</h3>
        <Link to="/" style={{ textDecoration: 'none', color: '#333' }}>🏠 Home</Link>
        <Link to="/detailsfillingpage/keyskills" style={{ textDecoration: 'none', color: '#333' }}>✏️ Edit Resume</Link>
        <button
          onClick={downloadComponentPDF}
          disabled={isLoading}
          style={{
            backgroundColor: '#28a745',
            color: '#fff',
            padding: '10px',
            border: 'none',
            cursor: 'pointer'
          }}
        >
          {isLoading ? 'Generating PDF...' : 'Download PDF'}
        </button>
      </div>

      {/* Main Content */}
      <div style={{ flex: 1, padding: '30px' }}>
        <div style={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          flexWrap: 'wrap',
          marginBottom: '30px'
        }}>
          <h2 style={{ color: '#07588a' }}>My Resume Preview</h2>
          {selectedTemplate && (
            <span style={{
              backgroundColor: '#d1ecf1',
              color: '#0c5460',
              padding: '10px',
              borderRadius: '5px',
              fontSize: '14px'
            }}>
              Current Template: {selectedTemplate}
            </span>
          )}
        </div>

        <div style={{ display: 'flex', gap: '20px', marginBottom: '20px', flexWrap: 'wrap' }}>
          <Link to="/detailsfillingpage/keyskills">
            <button style={{
              padding: '10px 20px',
              backgroundColor: 'transparent',
              border: '1px solid #07588a',
              color: '#07588a',
              cursor: 'pointer'
            }}>
              ← Go Back
            </button>
          </Link>

          <button
            onClick={downloadComponentPDF}
            disabled={isLoading}
            style={{
              padding: '10px 20px',
              backgroundColor: '#28a745',
              border: 'none',
              color: '#fff',
              cursor: 'pointer'
            }}
          >
            {isLoading ? 'Generating PDF...' : 'Save Resume'}
          </button>

          <button
            onClick={() => window.location.reload()}
            style={{
              padding: '10px 20px',
              backgroundColor: '#6c757d',
              border: 'none',
              color: '#fff',
              cursor: 'pointer'
            }}
          >
            Refresh Preview
          </button>
        </div>

        <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '40px' }}>
          <div
            id="divToPrint"
            style={{
              backgroundColor: '#fff',
              boxShadow: '0 0 10px rgba(0,0,0,0.1)',
              borderRadius: '10px',
              padding: '20px',
              width: '100%',
              maxWidth: '900px'
            }}
          >
            {renderTemplate()}
          </div>
        </div>

        <SuccessMessage showModal={showModal} setShowModal={setShowModal} />
      </div>
    </div>
  );
}

export default MyResume;
