import React, { useState } from 'react';
import Sidebar from './Sidebar'; // Adjust the import path if needed
import { templateImagesPaths } from '../Data/Data'; // your templates data

function Home() {
  // Example state or logic here if you have any
  const [isMouseOver, setIsMouseOver] = useState('');

  return (
    <div style={{ display: 'flex', minHeight: '100vh' }}>
      {/* Sidebar with fixed width */}
      <Sidebar />

      {/* Main content area */}
      <div className="content" style={{ flexGrow: 1, padding: '20px', backgroundColor: '#f7f7f7' }}>
        <h1>Resume Templates</h1>
        <div className="resume">
          {templateImagesPaths.map((template, index) => (
            <div
              key={index}
              onMouseEnter={() => setIsMouseOver(template.name)}
              onMouseLeave={() => setIsMouseOver('')}
              style={{
                marginBottom: '20px',
                border: isMouseOver === template.name ? '2px solid #4f81c7' : '1px solid #ccc',
                padding: '10px',
                borderRadius: '8px',
                backgroundColor: '#fff',
                cursor: 'pointer',
                maxWidth: '400px'
              }}
            >
              <img
                src={template.imageSource}
                alt={template.name}
                style={{ width: '100%', aspectRatio: '0.9', borderRadius: '8px' }}
              />
              <h3>{template.name}</h3>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Home;
