import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import { updateState } from '../../ReduxManager/dataStoreSlice';
const shortid = require('shortid');

const templateImagesPaths = [
  {
    name: "Modern Blue",
    imageSource: "https://via.placeholder.com/300x400/007bff/ffffff?text=Modern+Blue",
    category: "Modern",
  },
  {
    name: "Classic Black",
    imageSource: "https://via.placeholder.com/300x400/000000/ffffff?text=Classic+Black",
    category: "Classic",
  },
  {
    name: "Creative Purple",
    imageSource: "https://via.placeholder.com/300x400/6f42c1/ffffff?text=Creative+Purple",
    category: "Creative",
  },
];

function Home() {
  const [isMouseOver, setIsMouseOver] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('All');
  const dispatch = useDispatch();

  const categories = ['All', ...new Set(templateImagesPaths.map((t) => t.category))];

  const filteredTemplates = templateImagesPaths.filter(template => {
    const matchName = template.name.toLowerCase().includes(searchTerm.toLowerCase());
    const matchCategory = selectedCategory === 'All' || template.category === selectedCategory;
    return matchName && matchCategory;
  });

  const handleImageError = (e) => {
    e.target.onerror = null;
    e.target.src = "https://via.placeholder.com/300x400?text=No+Image";
  };

  return (
    <>
      <style>{`
        /* Container & Layout */
        .home-container {
          min-width: 320px;
          background: #f5f8ff;
          padding-bottom: 3rem;
          min-height: 100vh;
          font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
        }
        .header-title {
          background-color: #e9f5ff;
          border: 2px solid #007bff;
          font-size: 2rem;
          font-weight: 700;
          color: #007bff;
          padding: 1rem 2rem;
          border-radius: 12px;
          letter-spacing: 1px;
          width: fit-content;
          margin: 3rem auto 2rem auto;
          box-shadow: 0 2px 8px rgba(0,123,255,0.2);
          text-align: center;
        }

        /* Inputs */
        .search-input, .category-select {
          border-radius: 8px;
          padding: 0.6rem 1rem;
          font-size: 1rem;
          border: 2px solid #007bff;
          transition: border-color 0.3s ease;
        }
        .search-input:focus, .category-select:focus {
          outline: none;
          border-color: #0056b3;
          box-shadow: 0 0 6px #0056b3;
        }

        /* Template Card */
        .template-card {
          border: 1.5px solid #cce0ff;
          border-radius: 12px;
          overflow: hidden;
          transition: all 0.3s ease-in-out;
          cursor: pointer;
          background: white;
          height: 480px;
          display: flex;
          flex-direction: column;
          justify-content: space-between;
          box-shadow: 0 1px 5px rgba(0,0,0,0.1);
          position: relative;
        }
        .template-card:hover {
          box-shadow: 0 8px 20px rgba(0, 123, 255, 0.35);
          transform: translateY(-8px);
          border-color: #007bff;
        }

        /* Template Image */
        .template-image-container {
          flex-grow: 1;
          padding: 12px 12px 0 12px;
          display: flex;
          justify-content: center;
          align-items: center;
          position: relative;
        }
        .template-image-container img {
          max-height: 380px;
          max-width: 100%;
          object-fit: contain;
          border-radius: 10px;
          transition: transform 0.3s ease-in-out;
          display: block;
          box-shadow: 0 3px 8px rgba(0,0,0,0.15);
          user-select: none;
        }
        .template-image-container img:hover {
          transform: scale(1.03);
        }

        /* Buttons overlay on hover */
        .btn-overlay {
          position: absolute;
          top: 50%;
          left: 50%;
          transform: translate(-50%, -50%);
          z-index: 10;
          background-color: rgba(255, 255, 255, 0.95);
          padding: 0.8rem 1.2rem;
          border-radius: 12px;
          box-shadow: 0 4px 15px rgba(0, 123, 255, 0.25);
          display: flex;
          flex-direction: column;
          gap: 0.7rem;
          width: 90%;
          max-width: 220px;
          opacity: 0;
          pointer-events: none;
          transition: opacity 0.3s ease;
        }
        .template-card:hover .btn-overlay {
          opacity: 1;
          pointer-events: auto;
        }

        /* Buttons style */
        .btn-primary, .btn-outline-secondary {
          font-weight: 600;
          font-size: 0.9rem;
          border-radius: 8px;
          padding: 0.45rem 1rem;
          box-shadow: none;
          transition: background-color 0.3s ease, border-color 0.3s ease;
          width: 100%;
        }
        .btn-primary:hover {
          background-color: #0056b3;
          border-color: #00408a;
        }
        .btn-outline-secondary {
          border: 2px solid #6c757d;
          color: #6c757d;
          background-color: transparent;
        }
        .btn-outline-secondary:hover {
          background-color: #6c757d;
          color: white;
        }

        /* Template name header */
        .template-name {
          margin-top: 1rem;
          font-weight: 700;
          color: #212529;
          text-align: center;
          font-size: 1.25rem;
          padding: 0 0.5rem;
          user-select: none;
        }

        /* Responsive tweaks */
        @media (max-width: 767.98px) {
          .template-card {
            height: 430px;
          }
          .template-name {
            font-size: 1.1rem;
          }
          .header-title {
            font-size: 1.6rem;
            padding: 1rem 1.5rem;
          }
        }
      `}</style>

      <div className="home-container">
        <h3 className="header-title">
          Select a Resume Template to Get Started!
        </h3>

        <div className="container">
          <div className="row mb-4 align-items-center">
            <div className="col-md-6 mb-3 mb-md-0">
              <input
                type="text"
                className="search-input form-control"
                placeholder="Search template by name..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                autoComplete="off"
              />
            </div>
            <div className="col-md-6">
              <select
                className="category-select form-select"
                value={selectedCategory}
                onChange={(e) => setSelectedCategory(e.target.value)}
              >
                {categories.map((cat) => (
                  <option key={cat} value={cat}>
                    {cat}
                  </option>
                ))}
              </select>
            </div>
          </div>

          <div className="row">
            {filteredTemplates.length === 0 && (
              <div className="text-center mt-5 text-muted fs-5">
                No templates found.
              </div>
            )}

            {filteredTemplates.map((template) => (
              <div
                className="col-lg-3 col-md-6 col-12 mt-4 d-flex align-items-stretch"
                key={shortid.generate()}
                onMouseOver={() => setIsMouseOver(template.name)}
                onMouseOut={() => setIsMouseOver('')}
              >
                <div className="template-card shadow-sm">
                  <div className="template-name">{template.name}</div>

                  <div className="template-image-container">
                    <img
                      src={template.imageSource}
                      alt={`${template.name} Resume Template`}
                      onError={handleImageError}
                      style={{
                        transform: isMouseOver === template.name ? 'scale(1.03)' : 'scale(1)',
                      }}
                      draggable={false}
                    />
                    <div className="btn-overlay">
                      <Link to="/detailsfillingpage/personalinfo" className="mb-2">
                        <button
                          className="btn btn-primary"
                          title="Start with this template"
                          onClick={() =>
                            dispatch(
                              updateState({
                                key: 'selectedTemplate',
                                value: template.name,
                              })
                            )
                          }
                        >
                          Use Template
                        </button>
                      </Link>

                      <button
                        className="btn btn-outline-secondary"
                        title="Preview this template"
                        onClick={() => window.open(template.imageSource, '_blank')}
                      >
                        Preview
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </>
  );
}

export default Home;
