import React, { useState } from 'react';
import { useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import jsPDF from 'jspdf';
import html2canvas from 'html2canvas';
import Template1 from '../TemplatesComponents/Template1';
import Template2 from '../TemplatesComponents/Template2';
import Template3 from '../TemplatesComponents/Template3';
import Template4 from '../TemplatesComponents/Template4';
import SuccessMessage from './Modal';

function MyResume() {
  const selectedTemplate = useSelector((state) => state.dataStore.selectedTemplate);
  const [showModal, setShowModal] = useState(false);
  const [isLoading, setIsLoading] = useState(false);

  const downloadComponentPDF = () => {
    const input = document.getElementById('divToPrint');
    setIsLoading(true);
    html2canvas(input, { scrollY: -window.scrollY }).then((canvas) => {
      const imgData = canvas.toDataURL('image/png');
      const pdf = new jsPDF('p', 'px', 'a4');
      const ratio = canvas.width / canvas.height;
      const width = pdf.internal.pageSize.getWidth();
      const height = width / ratio;
      pdf.addImage(imgData, 'JPEG', 0, 0, width, height);
      pdf.save('resume.pdf');
    }).then(() => {
      setIsLoading(false);
      setShowModal(true);
      setTimeout(() => {
        setShowModal(false);
      }, 6000);
    });
  };

  const renderTemplate = () => {
    switch (selectedTemplate) {
      case 'Template 1':
        return <Template1 />;
      case 'Template 2':
        return <Template2 />;
      case 'Template 3':
        return <Template3 />;
      case 'Template 4':
        return <Template4 />;
      default:
        return (
          <div className="text-center text-danger fw-bold">
            <h3>Please select a template!</h3>
          </div>
        );
    }
  };

  return (
    <div className="container-fluid min-vh-100 bg-light p-4">
      <div className="d-flex justify-content-between align-items-center flex-wrap mb-4 px-3">
        <h2 className="text-primary fw-bold">My Resume Preview</h2>
        {selectedTemplate && (
          <span className="badge bg-info text-dark p-2 fs-6">
            Current Template: {selectedTemplate}
          </span>
        )}
      </div>

      <div className="d-flex justify-content-center gap-4 mb-4 flex-wrap">
        <Link to="/detailsfillingpage/keyskills">
          <button className="btn btn-outline-primary px-4 py-2" aria-label="Go back to edit resume">
            ‚Üê Go Back
          </button>
        </Link>

        <button
          className="btn btn-success px-4 py-2"
          onClick={downloadComponentPDF}
          disabled={isLoading}
          aria-label="Save resume as PDF"
        >
          {isLoading ? 'Generating PDF...' : 'Save Resume'}
        </button>

        <button
          className="btn btn-secondary px-4 py-2"
          onClick={() => window.location.reload()}
          aria-label="Refresh preview"
        >
          Refresh Preview
        </button>
      </div>

      <div className="resume-preview-wrapper d-flex justify-content-center mb-5">
        <div
          id="divToPrint"
          className="bg-white shadow rounded p-4"
          style={{ width: '100%', maxWidth: '900px' }}
        >
          {renderTemplate()}
        </div>
      </div>

      <SuccessMessage showModal={showModal} setShowModal={setShowModal} />
    </div>
  );
}

export default MyResume;
