import React from 'react';
import { useSelector } from 'react-redux';
const shortid = require('shortid');

function Template4() {
  const dataStore = useSelector(state => state.dataStore);

  return (
    <div className="w-100 p-4" style={{ backgroundColor: '#ffffff', fontFamily: 'Segoe UI, sans-serif', color: '#333' }}>
      <div className="row">
        {/* Sidebar */}
        <div className="col-4 p-4" style={{ backgroundColor: '#f4f4f4', borderRight: '2px solid #e0e0e0' }}>
          <div className="text-center">
            <img
              src={dataStore.imageFile}
              alt="profile"
              className="rounded-circle"
              style={{ width: '120px', height: '120px', objectFit: 'cover', marginBottom: '15px' }}
            />
            <h2 style={{ color: '#1e88e5' }}>{dataStore.personalInfo.firstName + ' ' + dataStore.personalInfo.lastName}</h2>
            <p style={{ fontWeight: 500 }}>{dataStore.workEx[dataStore.workEx.length - 1]?.title}</p>
          </div>

          <hr />
          <h5 className="mt-3">Contact</h5>
          <p><b>Email:</b> {dataStore.personalInfo.Email}</p>
          <p><b>Phone:</b> {dataStore.personalInfo.Mobile}</p>
          <p><b>Address:</b> {dataStore.personalInfo.Address1}, {dataStore.personalInfo.Address2}, {dataStore.personalInfo.City}, {dataStore.personalInfo.State}, {dataStore.personalInfo.Pin}</p>

          <h5 className="mt-4">Key Skills</h5>
          <ul>
            {dataStore.skills.map(skill => (
              <li key={shortid.generate()}>{skill.skillName}</li>
            ))}
          </ul>
        </div>

        {/* Main Content */}
        <div className="col-8 p-4">
          <div>
            <h3 style={{ color: '#1e88e5' }}>Profile Summary</h3>
            <p>{dataStore.personalInfo.Objective}</p>
          </div>

          <div className="mt-4">
            <h3 style={{ color: '#1e88e5' }}>Professional Experience</h3>
            {dataStore.workEx.map(item => (
              <div key={shortid.generate()} className="mb-3">
                <h5>{item.title} at {item.orgName}</h5>
                <p><i>{item.startYear} - {item.endYear}</i></p>
                <p>{item.jobDescription}</p>
              </div>
            ))}
          </div>

          <div className="mt-4">
            <h3 style={{ color: '#1e88e5' }}>Education</h3>
            {dataStore.education.map(item => (
              <div key={shortid.generate()} className="mb-3">
                <h5>{item.Degree}</h5>
                <p>{item.Type} from <b>{item.University}</b></p>
                <p>Duration: {item.Start} - {item.End}</p>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Template4;
