import React, { useState } from 'react'
import { Link } from 'react-router-dom'
import { templateImagesPaths } from '../Data/Data'
import { useDispatch } from 'react-redux'
import { updateState } from '../../ReduxManager/dataStoreSlice'
const shortid = require('shortid')

function Home() {
  const [isMouseOver, setIsMouseOver] = useState('MouseIsNotOver')
  const dispatch = useDispatch()

  return (
    <div style={{ minWidth: '300px', display: 'flex', gap: '20px', padding: '20px' }}>
      {/* Sidebar */}
      <aside
        style={{
          width: '250px',
          border: '1px solid #ddd',
          borderRadius: '8px',
          padding: '15px',
          height: 'fit-content',
          backgroundColor: '#f8f9fa',
          fontFamily: 'Segoe UI, sans-serif',
          color: '#1f4287',
        }}
      >
        <h4 className="mb-3" style={{ borderBottom: '1px solid #007bff', paddingBottom: '8px' }}>
          Sidebar Menu
        </h4>
        <ul style={{ listStyleType: 'none', paddingLeft: 0 }}>
          <li style={{ marginBottom: '10px' }}>
            <Link to="/" style={{ textDecoration: 'none', color: '#007bff' }}>
              Home
            </Link>
          </li>
          <li style={{ marginBottom: '10px' }}>
            <Link to="/templates" style={{ textDecoration: 'none', color: '#007bff' }}>
              Templates
            </Link>
          </li>
          <li style={{ marginBottom: '10px' }}>
            <Link to="/about" style={{ textDecoration: 'none', color: '#007bff' }}>
              About
            </Link>
          </li>
          <li style={{ marginBottom: '10px' }}>
            <Link to="/contact" style={{ textDecoration: 'none', color: '#007bff' }}>
              Contact
            </Link>
          </li>
        </ul>
      </aside>

      {/* Main content (existing template display) */}
      <div style={{ flexGrow: 1 }}>
        <div className="d-flex justify-content-center mt-5">
          <h3
            className="p-3 rounded shadow text-center text-primary fw-bold"
            style={{
              backgroundColor: '#f0f8ff', // aliceblue
              border: '2px solid #007bff',
              fontFamily: 'Segoe UI, sans-serif',
              fontSize: '1.5rem',
              letterSpacing: '1px',
              width: 'fit-content',
            }}
          >
            Select a Template to get started!
          </h3>
        </div>

        <div className="container" style={{ color: '#1f4287' }}>
          <div className="row">
            {templateImagesPaths.map((currentTemplate) => {
              return (
                <div
                  className="col col-lg-3 col-md-6  col-12 mt-5"
                  key={shortid.generate()}
                >
                  <div
                    style={{ position: 'relative' }}
                    onMouseOver={() => {
                      setIsMouseOver(currentTemplate.name)
                    }}
                    onMouseOut={() => {
                      setIsMouseOver('MouseIsNotOver')
                    }}
                  >
                    <div className="w-100 d-flex justify-content-center">
                      <h3>{currentTemplate.name}</h3>
                    </div>
                    <img
                      className="w-100 image-aspect-ratio"
                      src={currentTemplate.imageSource}
                      alt="template"
                    />
                    {isMouseOver === currentTemplate.name ? (
                      <Link to="/detailsfillingpage/personalinfo">
                        <button
                          className="btn btn-primary"
                          style={{ position: 'absolute', top: '50%', right: '30%' }}
                          onClick={() => {
                            dispatch(
                              updateState({
                                key: 'selectedTemplate',
                                value: currentTemplate.name,
                              })
                            )
                          }}
                        >
                          Use Template
                        </button>
                      </Link>
                    ) : null}
                  </div>
                </div>
              )
            })}
          </div>
        </div>
      </div>
    </div>
  )
}

export default Home
